import React from 'react';
import styled from 'styled-components';

import { Container } from '../../shared';

const MenuItemContainer = styled(Container)`
  background-color: purple;
  background-image: url(${(props) => props.imageUrl});
  ${(props) => {
    switch (props.size) {
      case 'medium':
        return 'width: 100%; height: 300px; grid-column: span 2';
      case 'large':
        return 'width: 100%; height: 450px';
      default:
        return 'width: auto';
    }
  }};

  background-position: center;
  background-size: cover;
`;

const MenuItem = ({ section }) => {
  return (
    <MenuItemContainer size={section.imageSize} imageUrl={section.imageUrl}>
      {/* <img src={section.imageUrl} alt={section.id} /> */}
      <div>
        <p>{section.title}</p>
        <a href={section.linkUrl}>SHOP NOW</a>
      </div>
    </MenuItemContainer>
  );
};

export default MenuItem;
