import styled from 'styled-components';

export const Button = styled.a`
  padding: 10px 35px;
  border: 1px solid white;
  border-radius: 10px;
  background-color: midnightblue;
  text-decoration: none;
  color: #fff;
  transition: all 0.1s ease-in;
  &:hover {
    background-color: #fff;
    color: midnightblue;
  }
`;
