import { combineReducers } from 'redux';

const INITIAL_STATE = {
  currentUser: null,
};

const currentUserReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case 'SET_CURRENT_USER':
      return action.payload;
    default:
      return state;
  }
};

export default combineReducers({
  currentUser: currentUserReducer,
});
