import { combineReducers } from 'redux';

const currentUserReducer = (state = null, action) => {
  switch (action.type) {
    case 'SET_CURRENT_USER':
      return action.payload;
    default:
      return state;
  }
};

export default combineReducers({
  currentUser: currentUserReducer,
});
