import React from 'react';
import styled from 'styled-components';

const FormInputLabel = styled.label`
  color: grey;
  font-size: 16px;
  font-weight: normal;
  position: absolute;
  pointer-events: none;
  left: 5px;
  top: 10px;
  transition: 300ms ease all;
  ${(props) =>
    props.value.length ? 'top: -14px; font-size: 12px; color: black' : null};
`;

const FormInput = ({ label, ...otherProps }) => {
  return (
    <div>
      <input {...otherProps} />
      {label ? (
        <FormInputLabel value={value.length}>{label}</FormInputLabel>
      ) : null}
    </div>
  );
};

export default FormInput;
